pragma solidity ^0.8.12;

import '../wallet/RWalletFactory.sol';
import '../wallet/RWallet.sol';
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract MarketPlace {

    RWalletFactory public immutable factoryContract;

    uint256 public treasury;

    uint256 public feeBase;

    uint256 public feeMultiplier;

    struct NFT {
        bool available;
        address lender;
        address contract_;
        uint256 id;
        uint256 fee;
        uint256 maxDuration;
    }

    mapping (address => NFT[]) private _assets;

    mapping(address => uint256) private _balances;

    event Loan(
        address indexed lender,
        address indexed borrower,
        address indexed nftContract,
        uint256 tokenId
    );

    constructor(
        address factoryAddress,
        uint256 _feeBase,
        uint256 _feeMultiplier
        ) {
        factoryContract = RWalletFactory(factoryAddress);
        feeBase = _feeBase;
        feeMultiplier = _feeMultiplier;
    }

    function getAssets(address lender) public view returns(NFT[] memory) {
        return _assets[lender];
    }

    function getBalance(address account) public view returns(uint256) {
        return _balances[account];
    }

    function isWallet(address account) public view returns(bool) {
        return factoryContract.isWallet(account);
    }

    function lendNFT(address contract_, uint256 tokenId, uint256 fee, uint256 maxDuration) public {
        require(_isApproved(contract_, tokenId), "Operator not approved");
        require(msg.sender == _getNFTowner(contract_, tokenId), "caller is not NFT owner");
        NFT memory newAsset = NFT(
            true,
            msg.sender,
            contract_,
            tokenId,
            fee,
            maxDuration
        );
        _assets[msg.sender].push(newAsset);
    }

    function borrowNFT(address lender, uint256 index, uint256 duration) public payable {
        require(isWallet(msg.sender), "caller is not a renter wallet contract");
        uint256 _maxDuration = _assets[lender][index].maxDuration;
        require(duration <= _maxDuration, "loan period set too long");
        uint256 fee = _assets[lender][index].fee;
        uint256 serviceFee = (fee * duration * feeMultiplier) / feeBase;
        require(msg.value >= (fee *duration)+ serviceFee, "not enough funds");
        uint256 tokenId = _assets[lender][index].id;
        address contract_ = _assets[lender][index].contract_;
        require(_operatorCount(msg.sender, contract_) <= 0, "error: operator count greater than zero" );
        require(_isApproved(contract_, tokenId), "error: token approval was revoked");

        _balances[lender] += fee * duration;
        treasury += serviceFee;
        IERC721 nftContract = IERC721(contract_);
        RWallet wallet = RWallet(payable(msg.sender));
        wallet.uponNFTLoan(
            address(nftContract),
            tokenId,
            lender,
            duration
        );
        _assets[lender][index].available = false;
        nftContract.transferFrom(lender, msg.sender, tokenId);
        emit Loan(lender, msg.sender, contract_, tokenId);
    }

    function withdraw() public {
        uint256 bal = _balances[msg.sender];
        require( bal > 0, "there is no balance for caller address");
        _balances[msg.sender] = 0;

        (bool success,) = payable(msg.sender).call{value: bal}("");
        require(success, "transfer of funds failed");
    }

    // CHANGE TO PRIVATE
    function _isApproved(address contract_, uint256 tokenId) public view returns(bool) {
        IERC721 nftContract = IERC721(contract_);
        address owner = nftContract.ownerOf(tokenId);
        return (
            nftContract.getApproved(tokenId) == address(this) ||
            nftContract.isApprovedForAll( owner , address(this) )
        );
    }

    function _operatorCount(address wallet, address contract_) public view returns(uint256) {
        RWallet _wallet = RWallet(payable(wallet));
        return _wallet.getOperatorCount(contract_);
    }

    function _getNFTowner(address contract_, uint256 tokenId) private view returns(address) {
        IERC721 nftContract = IERC721(contract_);
        return nftContract.ownerOf(tokenId);
    }

}","keccak256":"0x574fd190c788b236d2e6ce91244b1d87377c8d997023d4492b6911a82118647c","license":"SEE LICENSE IN LICENSE"},"contracts/samples/callback/TokenCallbackHandler.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.12;

/* solhint-disable no-empty-blocks */

import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "@openzeppelin/contracts/token/ERC777/IERC777Recipient.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";

/**
 * Token callback handler.
 *   Handles supported tokens' callbacks, allowing account receiving these tokens.
 */
contract TokenCallbackHandler is IERC777Recipient, IERC721Receiver, IERC1155Receiver {
    function tokensReceived(
        address,
        address,
        address,
        uint256,
        bytes calldata,
        bytes calldata
    ) external pure override {
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC1155Receiver.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC1155Receiver.onERC1155BatchReceived.selector;
    }

    function supportsInterface(bytes4 interfaceId) external view virtual override returns (bool) {
        return
            interfaceId == type(IERC721Receiver).interfaceId ||
            interfaceId == type(IERC1155Receiver).interfaceId ||
            interfaceId == type(IERC165).interfaceId;
    }
}
","keccak256":"0xfff3df5f5211d71158bb017ff791dc4fa85db53890f7bd72bac3a43d89e83752","license":"GPL-3.0"},"contracts/wallet/RWallet.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.12;

/* solhint-disable avoid-low-level-calls */
/* solhint-disable no-inline-assembly */
/* solhint-disable reason-string */

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import "../samples/MarketPlace.sol";
import "../core/BaseAccount.sol";
import "../samples/callback/TokenCallbackHandler.sol";

/**
  * minimal account.
  *  this is sample minimal account.
  *  has execute, eth handling methods
  *  has a single signer that can send requests through the entryPoint.
  */
contract RWallet is BaseAccount, TokenCallbackHandler, UUPSUpgradeable, Initializable {
    using ECDSA for bytes32;

    address public owner;

    bytes4 private constant TRANSFER_SEL = bytes4(keccak256(bytes("transferFrom(address,address,uint256)")));

    bytes4 private constant SAFE_TRANSFER_SEL = bytes4(keccak256(bytes("safeTransferFrom(address,address,uint256)")));

    bytes4 private constant SAFE_TRANSFER_DATA_SEL = bytes4(keccak256(bytes("safeTransferFrom(address,address,uint256,bytes)")));

    bytes4 private constant APPROVE_SEL = bytes4(keccak256(bytes("approve(address,uint256)")));

    bytes4 private constant APPROVE_ALL_SEL = bytes4(keccak256(bytes("setApprovalForAll(address,bool)")));

    bytes private constant EMPTY = bytes("");

    IEntryPoint private immutable _entryPoint;

    struct NFT {
        address contract_;
        uint256 id;
        address lender;
        bool borrowed;
        uint256 startTime;
        uint256 endTime;
    }

    event SimpleAccountInitialized(IEntryPoint indexed entryPoint, address indexed owner);

    NFT[] private _loans;

    mapping(address =>  NFT[]) private _loansByContract;

    mapping(address => uint256) private _operatorCount;

    modifier onlyOwner() {
        _onlyOwner();
        _;
    }

    /// @inheritdoc BaseAccount
    function entryPoint() public view virtual override returns (IEntryPoint) {
        return _entryPoint;
    }


    // solhint-disable-next-line no-empty-blocks
    receive() external payable {}

    constructor(IEntryPoint anEntryPoint) {
        _entryPoint = anEntryPoint;
        _disableInitializers();
    }

    function _onlyOwner() internal view {
        //directly from EOA owner, or through the account itself (which gets redirected through execute())
        require(msg.sender == owner || msg.sender == address(this), "only owner");
    }

    /**
     * execute a transaction (called directly from owner, or by entryPoint)
     */
    function execute(address dest, uint256 value, bytes calldata func) external {
        _requireFromEntryPointOrOwner();
        _call(dest, value, func);
    }

    /**
     * execute a sequence of transactions
     */
    function executeBatch(address[] calldata dest, bytes[] calldata func) external {
        _requireFromEntryPointOrOwner();
        require(dest.length == func.length, "wrong array lengths");
        for (uint256 i = 0; i < dest.length; i++) {
            _call(dest[i], 0, func[i]);
        }
    }

    /**
     * @dev The _entryPoint member is immutable, to reduce gas consumption.  To upgrade EntryPoint,
     * a new implementation of SimpleAccount must be deployed with the new EntryPoint address, then upgrading
      * the implementation by calling `upgradeTo()`
     */
    function initialize(address anOwner) public virtual initializer {
        _initialize(anOwner);
    }

    function _initialize(address anOwner) internal virtual {
        owner = anOwner;
        emit SimpleAccountInitialized(_entryPoint, owner);
    }

    // Require the function call went through EntryPoint or owner
    function _requireFromEntryPointOrOwner() internal view {
        require(msg.sender == address(entryPoint()) || msg.sender == owner, "account: not Owner or EntryPoint");
    }

    /// implement template method of BaseAccount
    function _validateSignature(UserOperation calldata userOp, bytes32 userOpHash)
    internal override virtual returns (uint256 validationData) {
        bytes32 hash = userOpHash.toEthSignedMessageHash();
        if (owner != hash.recover(userOp.signature))
            return SIG_VALIDATION_FAILED;
        return 0;
    }

    function _call(address target, uint256 value, bytes memory data) internal {
        require(_beforeCallCheck(target, data), "Unauthorized operation");
        (bool success, bytes memory result) = target.call{value : value}(data);
        if (!success) {
            assembly {
                revert(add(result, 32), mload(result))
            }
        }
    }

    /**
     * @dev this function is run before every call
     * it returns a bool indicating weather or not the call is authorized
     * if there are enough assets marked in the _loansByContract list will reach gas limit
     * in such cases releaseAsset methods should be called
     */
    function _beforeCallCheck(address target, bytes memory data) private returns(bool) {
        if (data.length < 4) return true;
        bytes4 funcSel = _extractFunctionSignature(data);
        if(funcSel == APPROVE_ALL_SEL){
            if(_loansByContract[target].length > 0) return false;
            bool approved;
            assembly {
                approved := mload(add(data, 68))
            }
            if(approved) _operatorCount[target]++;
            else {
                address operator;
                assembly{
                    operator := mload(add(data, 36))
                }
                if(!approved) {
                    if(_isApprovedForAll(target, operator)) _operatorCount[target]--;
                }
            }
            return true;
        }
        if(
            funcSel == TRANSFER_SEL ||
            funcSel == SAFE_TRANSFER_SEL ||
            funcSel == SAFE_TRANSFER_DATA_SEL
        ) {
            address from;
            uint256 tokenId;
            assembly {	     
	            from := mload(add(data, 36))
	            tokenId := mload(add(data, 100))
	        }
            if(from == address(this)){
                return !_isLoan(target, tokenId);
            }
        }
        if(funcSel == APPROVE_SEL) {
            uint256 tokenId;
            assembly{
                tokenId := mload(add(data, 68))
            }
            return !_isLoan(target, tokenId);
        }

        return true;
    }

    function _isLoan(address contract_, uint256 tokenId) private view returns(bool){
        NFT[] memory loans_ = _loansByContract[contract_];
        for(uint i=0; i<loans_.length; i++){
            if(loans_[i].id == tokenId){
                return true;
            }
        }
        return false;
    }

    function _extractFunctionSignature(bytes memory data) private pure returns (bytes4) {
        // require(data.length >= 4, "Invalid data length");
        bytes4 functionSelector;
        assembly {
            functionSelector := mload(add(data, 32))
        }
        return functionSelector;
    }


    function getLoans() external view returns(NFT[] memory) {
        // return _loans;
    }

    function getLoansByContract(address contract_) external view returns(NFT[] memory) {
        return _loansByContract[contract_];
    }

    function getOperatorCount(address contract_) public view returns(uint256) {
        return _operatorCount[contract_];
    }

    /**
     * To be called upon a rental tx by marketplace contract
     * this a requirement for marketplace to be used
     */
    function uponNFTLoan(address _contract, uint256 id, address owner_, uint256 duration) external {
        NFT memory newAsset = NFT(
            _contract,
            id,
            owner_,
            true,
            block.timestamp,
            block.timestamp + duration
        );
        // _loans.push(newAsset);
        _loansByContract[_contract].push(newAsset);
    }

    /**
     * releaseAsset methods
     * releases asset(s) from wallet.
     * should be called by owner in case loans make operations reach gas limit.
     * this might happen because `_isLoan` needs to be called at every userOp
     */
    function releaseSingleAsset(address contract_, uint256 index) public onlyOwner {
        _releaseAsset(contract_, index);
    }

    // function releaseMultipleAssets(uint256[] memory indexes) public onlyOwner {
    //     for(uint256 i=0; i<indexes.length; i++) {
    //         _releaseAsset(indexes[i]);
    //     }
    // }

    function _releaseAsset(address contract_, uint256 index) private {
        IERC721 nftContract = IERC721(_loansByContract[contract_][index].contract_);
        nftContract.safeTransferFrom(
            address(this),
            _loansByContract[contract_][index].lender,
            _loansByContract[contract_][index].id);
        _subAssetFromList(contract_, index);
    }

    function _subAssetFromList(address contract_, uint256 index) private returns(uint256) {
        uint256 lastIndex = _loansByContract[contract_].length -1;
        _loansByContract[contract_][index] = _loansByContract[contract_][lastIndex];
        _loansByContract[contract_].pop();
        return lastIndex;
    }

    /**
     * @dev allows any caller to pull asset back to original owner in exchange for a fee
     * checks if assest is a loan and if loan end time was reached
     */
    function pullAsset(address contract_, uint256 index) external {
        require(
            block.timestamp > _loansByContract[contract_][index].endTime,
            "Loan duration not reached"
        );
        _releaseAsset(contract_, index);
    }

    // check: gas efficient alternatives
    // CHANGE: use .call to save gas
    function _getNFTowner(address contract_, uint256 tokenId) private view returns(address) {
        IERC721 nftContract = IERC721(contract_);
        return nftContract.ownerOf(tokenId);
    }

    // CHANGE: use .call to save gas
    function _isApproved(address contract_, uint256 tokenId) private view returns(bool) {
        IERC721 nftContract = IERC721(contract_);
        return nftContract.getApproved(tokenId) != address(0);
    }

    function _isApprovedForAll(address contract_, address operator) private view returns(bool) {
        IERC721 nftContract = IERC721(contract_);
        return nftContract.isApprovedForAll(address(this), operator);
    }


    /**
     * check current account deposit in the entryPoint
     */
    function getDeposit() public view returns (uint256) {
        return entryPoint().balanceOf(address(this));
    }

    /**
     * deposit more funds for this account in the entryPoint
     */
    function addDeposit() public payable {
        entryPoint().depositTo{value : msg.value}(address(this));
    }

    /**
     * withdraw value from the account's deposit
     * @param withdrawAddress target to send to
     * @param amount to withdraw
     */
    function withdrawDepositTo(address payable withdrawAddress, uint256 amount) public onlyOwner {
        entryPoint().withdrawTo(withdrawAddress, amount);
    }

    function _authorizeUpgrade(address newImplementation) internal view override {
        (newImplementation);
        _onlyOwner();
    }
}

","keccak256":"0xac7ade568f07af78c7a980cf8e50b495dfdc660adcf2443c1e9a1b520e3be6f7","license":"GPL-3.0"},"contracts/wallet/RWalletFactory.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.12;

import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

import "./RWallet.sol";
import "../interfaces/IEntryPoint.sol";

/**
 * A UserOperations "initCode" holds the address of the factory, and a method call (to createAccount, in this sample factory).
 * The factory's createAccount returns the target account address even if it is already installed.
 * This way, the entryPoint.getSenderAddress() can be called either before or after the account is created.
 */
contract RWalletFactory {
    RWallet public immutable accountImplementation;

    mapping (address => bool) private _isWallet;

    event WalletCreated(address indexed owner, address indexed account);

    constructor(IEntryPoint _entryPoint) {
        accountImplementation = new RWallet(_entryPoint);
    }

    /**
     * checks weather or not an address is a wallet created via this contract
     */
    function isWallet(address account) public view returns(bool) {
        return _isWallet[account];
    }

    /**
     * create an account, and return its address.
     * returns the address even if the account is already deployed.
     * Note that during UserOperation execution, this method is called only if the account is not deployed.
     * This method returns an existing account address so that entryPoint.getSenderAddress() would work even after account creation
     */
    function createAccount(address owner,uint256 salt) public returns (RWallet ret) {
        address addr = getAddress(owner, salt);
        uint codeSize = addr.code.length;
        if (codeSize > 0) {
            return RWallet(payable(addr));
        }
        _isWallet[addr] = true; // saves address as a created Wallet
        emit WalletCreated(owner, addr);
        ret = RWallet(payable(new ERC1967Proxy{salt : bytes32(salt)}(
                address(accountImplementation),
                abi.encodeCall(RWallet.initialize, (owner))
            )));
    }

    /**
     * calculate the counterfactual address of this account as it would be returned by createAccount()
     */
    function getAddress(address owner,uint256 salt) public view returns (address) {
        return Create2.computeAddress(bytes32(salt), keccak256(abi.encodePacked(
                type(ERC1967Proxy).creationCode,
                abi.encode(
                    address(accountImplementation),
                    abi.encodeCall(RWallet.initialize, (owner))
                )
            )));
    }

    function stake(address entryPoint, uint32 unstakeDelaySec) external payable {
        (bool success, ) = entryPoint.call{value: msg.value}(
            abi.encodeWithSignature("addStake(uint32)", unstakeDelaySec)
        );
        require(success, "stake call failed");
    }
}
